frappe.ui.form.on('feed_back', {
	refresh(frm) {

	frm.add_custom_button('user' ,function() {

	    frappe.prompt([

	    {
        fieldname:'firstname',
        label:'firstname',
        fieldtype:'Data',
        reqd:1,

        },


        {
        fieldname:'lastname',
        label:'lastname',
        fieldtype:'Data'

        },

        {
        fieldname:'dob',
        label:'DOB',
        fieldtype:'Date'

        },

        {
        fieldname:'age',
        label:'AGE',
        fieldtype:'Data'

        },

        {
        fieldname:'gender',
        label:'gender',
        fieldtype:'Select',
        options:'Male\nFemale\n',

        },

	    ],

	    function(praful){



	        frm.set_value('firstname',praful.firstname)
            frm.set_value('lastname',praful.lastname)
            frm.set_value('dob',praful.dob)
            frm.set_value('age',praful.age)
            frm.set_value('gender',praful.gender)


        }  
	    )    




	    }).addClass('btn-primary')
	}, 

	    check(frm)
	    {
	    if(frm.doc.check)
	        {

            frm.set_value('place1',frm.doc.place)
            frm.set_value('address1',frm.doc.address)

	        }
	        else {
                 frm.set_value('address1')
                 frm.set_value('place1')


	        }

	}
})

frappe.ui.form.on('feedback_purchase', {
	    before_submit: async (frm) => {
        let prompt = new Promise((resolve, reject) => {
            frappe.confirm(
                'Are you sure?',
                () => resolve(),
                () => reject()
            );
        });
        await prompt.then(
            () => frappe.show_alert("Submitted", 3), 
            () => {
                frappe.validated = false;
                frappe.show_alert("Not submitted", 3)
            }
        );
    }
})

