                frappe.ui.form.on('form_customer', {
    refresh(frm) {
        frm.add_custom_button('Create User', function () {
            frappe.prompt([
                {
                    label: 'First Name',
                    fieldname: 'firstname',
                    fieldtype: 'Data',
                },
                {
                    label: 'Last Name',
                    fieldname: 'lastname',
                    fieldtype: 'Data',
                },
                {
                    label: 'E-mail',
                    fieldname: 'email',
                    fieldtype: 'Data',
                },
                {
                    label: 'Phone',
                    fieldname: 'phone',
                    fieldtype: 'Data',
                },
                {
                    label: 'DOB',
                    fieldname: 'dob',
                    fieldtype: 'Date',
                }

            ], function (values) {
                var newDoc = frappe.model.get_new_doc("doc_form_customer"); // Replace 'Your_DocType_Name' with the correct doctype name
                newDoc.firstname = values.firstname;
                newDoc.lastname = values.lastname;
                newDoc.email=values.email;
                newDoc.phone=values.phone;
                newDoc.dob=values.dob;

                frappe.db.insert(newDoc).then((response) => {
                    if (!response.exc) {
                        frappe.msgprint('User created successfully.');
                        frm.reload_doc();
                    } else {
                        frappe.msgprint('Error creating user: ' + response.exc);
                    }
                });
            }, 'Enter User Details', 'Submit');
        });
    }
});

frappe.ui.form.on('webeaz', {
    event: function(frm) {
        frappe.call({
    method: 'frappe.client.get_list',
    args: {
        doctype: 'webeaz_event', // Specify the name of the DocType you want to fetch from
        filters: { "name":frm.doc.event},

        fields:["description2","name"] // Specify the name of the field you want to retrieve
    },
    callback: function(response) {
        if (response && response.message) {
            console.log(response);

            // Do something with the fetched value
             cur_frm.set_value('description',response.message[0].description2);
            console.log(response.message.description);
        }
    }
});


    }
});
















