function updateFullName(frm) {
    var first = frm.doc.first_name || "";
    var middle = frm.doc.middle_name || "";
    var last = frm.doc.last_name || "";

    // Generate a random number between 1 and 1000
    var randomNum = 'STD-' + Math.floor(Math.random() * 1000);

    var full_name = first;

    if (middle) {
        full_name += " " + middle;
    }

    full_name += " " + last;

    frm.set_value('custom_student_full_name_', full_name.trim());
    // Add the random number to the full name
    full_name += " - " + randomNum;

    frm.set_value('custom_full_name', full_name.trim());
}

