frappe.ui.form.on('Course Status', {
    student_group: function(frm) {
        // Clear existing rows in the topic_table
        frm.clear_table('topic_table');

        var studentGroup = frm.doc.student_group;

        // Check if student_group is selected
        if (studentGroup) {
            frappe.call({
                method: 'frappe.client.get',
                args: {
                    doctype: 'Student Group',
                    name: studentGroup
                },
                callback: function(response) {
                    var studentGroupDoc = response.message;

                    // Make the student_group field readonly
                    frm.set_df_property('student_group', 'read_only', 1);

                    frappe.call({
                        method: 'frappe.client.get',
                        args: {
                            doctype: 'Program',
                            name: studentGroupDoc.program
                        },
                        callback: function(progResponse) {
                            var programDoc = progResponse.message;
                            var courseList = programDoc.courses;

                            for (var i = 0; i < courseList.length; i++) {
                                frappe.call({
                                    method: 'frappe.client.get',
                                    args: {
                                        doctype: 'Course',
                                        name: courseList[i].course_name
                                    },
                                    callback: function(courseResponse) {
                                        var courseDoc = courseResponse.message;
                                        var topicsList = courseDoc.topics;

                                        topicsList.forEach(function(t) {
                                            var row = frappe.model.add_child(frm.doc, 'Topics_c', 'topic_table');
                                            row.topics_name = t.topic;
                                            row.topics_full_name = t.topic;
                                        });

                                        // Refresh the field to display the updated child table
                                        frm.refresh_field('topic_table');
                                    }
                                });
                            }
                        }
                    });
                }
            });
        } else {
            // If student_group is not selected, make the field writable
            frm.set_df_property('student_group', 'read_only', 0);
        }
    }
});


