frappe.ui.form.on('Instructor', {
    custom_schedule: function(frm) {
        if (frm.doc.custom_schedule == true) {
            frappe.model.clear_table(frm.doc, 'custom_schedules');
            var instructorName = frm.doc.instructor_name;

            frappe.call({
                method: 'frappe.client.get_list',
                args: {
                    doctype: 'Course Schedule',
                    filters: {
                        'instructor_name': instructorName
                    },
                    order_by: 'schedule_date asc'
                },
                callback: function(response) {
                    var courseSchedules = response.message;
                    console.log(courseSchedules);

                    if (courseSchedules && courseSchedules.length > 0) {
                        var scheduleArray = [];

                        courseSchedules.forEach(function(schedule) {
                            frappe.call({
                                method: 'frappe.client.get',
                                args: {
                                    doctype: 'Course Schedule',
                                    order_by: 'schedule_date desc',
                                    name: schedule.name,
                                },
                                callback: function(response) {
                                    var scheduleRecord = response.message;
                                    console.log(scheduleRecord);

                                    if (scheduleRecord) {
                                        scheduleArray.push({
                                            crs: scheduleRecord.course,
                                            sg: scheduleRecord.student_group,
                                            d: scheduleRecord.schedule_date,
                                            st: scheduleRecord.from_time,
                                            et: scheduleRecord.to_time
                                        });

                                        // Check if all schedules are processed before saving
                                        if (scheduleArray.length === courseSchedules.length) {
                                            // Sort the array by schedule date
                                            scheduleArray.sort((a, b) => new Date(a.d) - new Date(b.d));

                                            // Add the sorted data to the child table
                                            scheduleArray.forEach(function(item) {
                                                var row = frappe.model.add_child(frm.doc, 'custom_schedules');
                                                row.crs = item.crs;
                                                row.sg = item.sg;
                                                row.d = item.d;
                                                row.st = item.st;
                                                row.et = item.et;
                                            });

                                            // Refresh the child table field
                                            frm.refresh_field('custom_schedules');

                                            // Save the document after adding all child records
                                            frm.save();
                                        }
                                    } else {
                                        frappe.msgprint("Error fetching schedule details");
                                    }
                                }
                            });
                        });
                    } else {
                        frappe.msgprint("No Schedules");
                    }
                }
            });
        } else {
            frappe.model.clear_table(frm.doc, 'custom_schedules');

            // Save the document only if it is marked as unsaved
            if (frm.doc.__unsaved) {
                frm.save();
            }
        }
    }
});

