frappe.ui.form.on('Fees', {
    program_enrollment: function(frm) {
        var program_enroll = frm.doc.program_enrollment;
        console.log(program_enroll);

        if (program_enroll) {
            frappe.call({
                method: 'frappe.client.get',
                args: {
                    doctype: 'Program Enrollment',
                    filters: {
                        'name': program_enroll
                    }
                },
                callback: function(response) {
                    console.log(response.message);
                    if (response.message) {
                        var Academic_year = response.message.academic_year;
                        var Batch = response.message.student_batch_name;

                        console.log(Academic_year);
                        console.log(Batch);

                        // Set the values in the form
                        frm.set_value('academic_year', Academic_year);
                        frm.set_value('custom_batch', Batch);

                        // Calculate due_date as today + 10 days
                        var today = frappe.datetime.nowdate();
                        var due_date = frappe.datetime.add_days(today, 10);

                        // Set the due_date field
                        frm.set_value('due_date', due_date);
                    } else {
                        console.log("Program Enrollment not found");
                    }
                }
            });
        } else {
            console.log("Program Enrollment is empty");
        }
    }
});







