frappe.ui.form.on('C_opportunity', {
    refresh: function(frm) {
        // Add a custom button to delete the documents based on Full Name
        frm.add_custom_button(__('Delete Documents'), function() {
            var full_name = frm.doc.full_name;

            // Search for the C_Lead document based on Full Name
            frappe.call({
                method: 'frappe.client.get_list',
                args: {
                    'doctype': 'C_Lead',
                    'filters': {
                        'full_name': full_name
                    },
                    'fields': ['name']
                },
                callback: function(response) {
                    if (response.message && response.message.length > 0) {
                        var c_lead_name = response.message[0].name;

                        // Confirm with the user before deleting the documents
                        frappe.confirm(
                            __('Are you sure you want to delete these documents?'),
                            function() {
                                // If the user confirms, delete the C_opportunity document
                                frappe.call({
                                    method: 'frappe.client.delete',
                                    args: {
                                        'doctype': 'C_opportunity',
                                        'name': frm.doc.name
                                    },
                                    callback: function(response) {
                                        if (!response.exc) {
                                            // Delete the related C_Lead document
                                            frappe.call({
                                                method: 'frappe.client.delete',
                                                args: {
                                                    'doctype': 'C_Lead',
                                                    'name': c_lead_name
                                                },
                                                callback: function(response) {
                                                    if (!response.exc) {
                                                        frappe.msgprint(__('Documents deleted successfully.'));
