frappe.ui.form.on('Student Applicant', {
    paid: function(frm) {
        // Check if the 'paid' checkbox is checked (value equals 1)
        if (frm.doc.paid === 1) {
            frm.set_value('custom_status', 'CONVERTED');

            var lead = frm.doc.custom_opp_name;
            console.log("Opportunity name:", lead);

            // Update Opportunity
            frappe.call({
                method: "frappe.client.get",
                args: {
                    "doctype": "C_opportunity",
                    "name": lead,
                },
                callback: function(response) {
                    var leadObj = response.message;

                    if (leadObj) {
                        console.log("Opportunity Status before update:", leadObj.status);

                        // Update the 'status' field of the Opportunity
                        leadObj.status = "CONVERTED";

                        // Save the changes to the Opportunity
                        frappe.call({
                            method: "frappe.client.save",
                            args: {
                                "doc": leadObj
                            },
                            callback: function(saveResponse) {
                                if (saveResponse.exc) {
                                    console.error("Error updating Opportunity Status:", saveResponse.exc);
                                } else {
                                    console.log("Opportunity Status updated to 'Converted'");
                                    // Save the current document after updating the Opportunity status
                                    frm.save();
                                }
                            }
                        });
                    } else {
                        console.error("Opportunity not found.");
                    }
                }
            });
        } else {
            console.log("The 'paid' checkbox is not checked. Skipping Opportunity update.");
        }
    }
});
